import React, { useState, useEffect } from "react";

const storedTodos = localStorage.getItem("todos");
  let initialTodos = [];
  if (storedTodos) {
    initialTodos = JSON.parse(storedTodos);
  }
const List = () => {

  

  const [todos, setTodos] = useState([initialTodos]);
  const [todoInput, setTodoInput] = useState("");

  useEffect(() => {
    const storedTodos = localStorage.getItem("todos");
    if (storedTodos) {
      setTodos(JSON.parse(storedTodos));
    }
  }, []);

  useEffect(() => {
    localStorage.setItem("todos", JSON.stringify(todos));
  }, [todos]);

  const style = {
    spacing: {
      margin: "10px",
      width: "600px",
    },
    middle: {
      minHeight: "calc(86vh - 35px)",
      padding: "10px",
    },
    completed: {
      textDecoration: "line-through",
    },
  };

  const handleAddTodo = () => {
    if (todoInput.trim() !== "") {
      const newTodo = {
        id: todos.length + 1,
        description: todoInput.trim(),
        check: false,
      };
      setTodos([...todos, newTodo]);
      setTodoInput("");
      localStorage.setItem('todos', JSON.stringify([...todos, newTodo]));
    } else {
      alert("Please enter a todo");
    }
  };

  const ChangeInputText = (e) => {
    setTodoInput(e.target.value);
  };

  const handleToggleTodo = (id) => {
    const updatedTodos = todos.map(
      (todo) => (todo.id === id ? { ...todo, check: !todo.check } : todo)
    );
    setTodos(updatedTodos);
  };

  const DeleteTodo = (id) => {
    const updatedTodos = todos.filter((todo) => todo.id !== id);
    const reindexedTodos = updatedTodos.map((todo, index) => ({
      ...todo,
      id: index + 1,
    }));
    setTodos(reindexedTodos);
    localStorage.setItem('todos', JSON.stringify(reindexedTodos));

  };

  return (
    <div
      style={{ ...style.spacing, ...style.middle }}
      className="mx-auto p-4 bg-white rounded-xl flex flex-col space-y-4"
    >
      <h2 className="text-center text-xl font-medium">Todo List</h2>
      <div className="flex items-center">
        <input
          type="text"
          placeholder="Enter your todo"
          className="flex-grow border border-gray-300 rounded-md py-2 px-3 mr-4"
          value={todoInput}
          onChange={ChangeInputText}
        />
        <button
          className="bg-purple-500 text-white px-4 py-2 rounded-md"
          onClick={handleAddTodo}
        >
          Add
        </button>
      </div>
      <ul>
        {todos.map((myvalue) => (
          <li
            key={myvalue.id}
            className={`flex items-center justify-between py-2 border-b border-gray-200 ${
              myvalue.check ? "completed" : ""
            }`}
          >
            <label className="cursor-pointer">
              <input
                type="checkbox"
                className="mr-4"
                checked={myvalue.check}
                onChange={() => handleToggleTodo(myvalue.id)}
              />
              <span style={myvalue.check ? style.completed : {}}>
                {myvalue.id} {myvalue.description}
              </span>
            </label>
            <button
              className="bg-purple-500 text-white px-4 py-2 rounded-md"
              onClick={() => DeleteTodo(myvalue.id)}
            >
              Delete
            </button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default List;
