import React from 'react';

const style = {
  spacing: {
    margin: "10px",
    width: "600px",
    display: "flex",
    flexDirection: "column",
  },
  middle: {
    // padding: "8px", // Remove minHeight property
    // display: "flex",
    // flexDirection: "column",
    minHeight: "81vh", // Set minimum height to fill the entire viewport
  }
};

const List = () => {
  return (
    <div style={{ ...style.spacing, ...style.middle }}>
      <h2 className="text-center text-xl font-medium">Todo List</h2>
      <div className="flex items-center">
        <input
          type="text"
          placeholder="Enter your todo"
          className="border border-gray-300 rounded-md py-2 px-3 mr-4"
        />
        <button className="bg-purple-500 text-white px-4 py-2 rounded-md">Add</button>
      </div>
    </div>
  );
};

export default List;
