import React, { useState } from "react";

const style = {
  spacing: {
    margin: "10px",
    width: "600px",
  },
  middle: {
    minHeight: "calc(86vh - 35px)",
    padding: "10px",
  },
};

const List = () => {
  const [todos, setTodos] = useState([]);
  const [todoInput, setTodoInput] = useState("");

  const handleAddTodo = () => {
    if (todoInput.trim() !== "") {
      const newTodo = {
        id: todos.length + 1,
        description: todoInput.trim(),
        completed: false, // Initially set to false
      };
      setTodos([...todos, newTodo]);
      setTodoInput("");
    } else {
      alert("Please enter a todo");
    }
  };

  const ChangeInputText = (e) => {
    setTodoInput(e.target.value);
  };

  const handleToggleTodo = (id) => {
    const updatedTodos = todos.map((todo) =>
      todo.id === id ? { ...todo, completed: !todo.completed } : todo
    );
    console.log("Updated Todos:", updatedTodos); // Check if todos are being updated correctly
    setTodos(updatedTodos);
  };
  

  return (
    <div
      style={{ ...style.spacing, ...style.middle }}
      className="mx-auto p-4 bg-white rounded-xl flex flex-col space-y-4"
    >
      <h2 className="text-center text-xl font-medium">Todo List</h2>
      <div className="flex items-center">
        <input
          type="text"
          placeholder="Enter your todo"
          className="flex-grow border border-gray-300 rounded-md py-2 px-3 mr-4"
          value={todoInput}
          onChange={ChangeInputText}
        />
        <button
          className="bg-purple-500 text-white px-4 py-2 rounded-md"
          onClick={handleAddTodo}
        >
          Add
        </button>
      </div>
      <ul>
        {todos.map((myvalue) => (
          <li
            key={myvalue.id}
            className="flex items-center justify-between py-2 border-b border-gray-200"
          >
            <label className="cursor-pointer">
              <input
                type="checkbox"
                className="mr-4"
                checked={myvalue.completed}
                onChange={() => handleToggleTodo(myvalue.id)}
              />
              {myvalue.description}
            </label>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default List;
